  }) 
  response.catch((error)=>{
   console.log(error.response,'action error ');
  })

       return{
         type: 'LOGIN_USER',
         payload: response
     };
   }  


   export const logOut= () => {
    return{
        type: 'LOG_OUT'
    };
};



