import React from 'react';
import {connect} from 'react-redux';
import { postList , editPost } from '../actions';
import CreatePost from './CreatePost';
import history from './history'


class EditPost extends React.Component {

    onSubmit = (formValue) => {
        this.props.editPost(this.props.match.params.id,formValue)   
        this.props.history.push('/PostList')
 };

 renderData(){

   if(localStorage.getItem("token") && this.props.match.params.id){
     if((this.props.post === undefined) ){
       return (
        history.push('/')
       )
     }else{

     return (
       <div>
         <h3>Update post</h3>
         <CreatePost initialValues={{title:this.props.post.title.rendered,content:this.props.post.content.rendered,status:this.props.post.status}} onSubmit={this.onSubmit} />
       </div>      
     )
     }
    }

 }

render() {  
   return(
        <div>
         {this.renderData()}        
        </div>
     );  
   }
}

const mapStateToProps = (state,ownProps)=>{
   console.log(state.posts[ownProps.match.params.id])
   return {
      post:state.posts[ownProps.match.params.id]
   }
}

export default connect(mapStateToProps ,{postList ,editPost })(EditPost);
