   const headers = {
                 'Content-Type': 'application/json',
                   }

   const response = api.post('/wp/v2/users/register',values,{headers:headers});
          response.then((res)=>{
          callback(res);

          toastr.success( 'successfully signup')
      }) 
        response.catch((error)=>{
        toastr.error('invalid user');
     })
        return{
        type: 'CREATE_USER',
        payload: response
    };
  }  



