        toastr.error('invalid user');
    })

       return{
         type: 'LOGIN_USER',
         payload: response
     };
   }  


  export const postList =(callback) => {                               
      const response = api.get('/wp/v2/posts/');
      response.then((res)=>{
      callback(res);
    }) ;
      response.catch((error)=>{
      toastr.error('invalid user');
