import _ from  'lodash';
import React from 'react';
import {connect} from 'react-redux';
import { postList , editPost } from './actions';
import CreatePost from './CreatePost';


class EditPost extends React.Component {
    componentDidMount() {
        this.props.postList(this.props.match.params.id);
    }

    onSubmit = (formValue) => {
        this.props.editPost(this.props.match.params.id,formValue)   
    };

render() {
  if(!this.props.stream) {
      return <div>Loading........</div>;
  }
   return(
        <div>
            <h3>Edit a Post</h3>
            <CreatePost
                 initialValues={_.pick(this.props.stream,'title','description')}
                 onSubmit={this.onSubmit}
            />        
        </div>
     );  
   }
}

const mapStateToProps = (state,ownProps) => {
      return {stream : state.streams[ownProps.match.params.id] };
};

export default connect(mapStateToProps , {postList ,editPost })(EditPost);
