import React from 'react';
import {Field,reduxForm} from 'redux-form';
import {createUser} from '../actions/index';
import { connect } from "react-redux";
import renderField from './utils/RenderField';
import {Link} from 'react-router-dom';

class SignUpForm extends React.Component{

    onSubmit(values){

        this.props.createUser(); 
        console.log(values); 

    }

    render(){

        return(
          <form onSubmit = {this.props.handleSubmit(this.onSubmit)}>
                <h1>SignUp</h1>
                <div>
                    <label>First Name</label>
                    <div>
                    <Field
                        name="firstName"
                        component={renderField}
                        type="text"
                        placeholder="First Name"                    
                    />
                    </div>
                </div>
                <div>
                    <label>Last Name</label>
                    <div>
                    <Field
                        name="lastName"
                        component={renderField}
                        type="text"
                        placeholder="Last Name"
                    />
                    </div>
                </div>  
                <div>
                    <label>Email</label>
                    <div>
                    <Field
                        name="email"
                        component={renderField}
                        type="email"
                        placeholder="Email"
                    />
                    </div>
                </div>
                <div>
                    <label>UserName</label>
                    <div>
                    <Field
                        name="UserName"
                        component={renderField}
                        type="UserName"
                        placeholder="UserName"
                    />
                    </div>
                </div>
                <div>
                    <label>PassWord</label>
                    <div>
                    <Field
                        name="PassWord"
                        component={renderField}
                        type="PassWord"
                        placeholder="PassWord"
                    />
                    </div>
                </div>
                <div>
                    <label>ConfirmPassWord</label>
                    <div>
                    <Field
                        name="ConfirmPassWord"
                        component={renderField}
                        type="PassWord"
                        placeholder="re-enter PassWord"
                    />
                    </div>
                </div>
                <div>
                    <button type="submit">
                    Submit 
                    </button>
                    <Link to="/Login" className="ui button primary">
                        back to Login   
                    </Link>
                </div>
            </form>

        );
    }
}

const  validate = (values) => {
    const errors = {}

console.log(values.firstName)
     if(!values.firstName) {
        errors.firstName='you must enter a firstName';
    } else if(values.firstName.length > 15){
        errors.firstName="you must enter less than 15 letters"
    }

     if(!values.lastName){
        errors.lastName='you must enter a lastName';
     }else if(values.lastName.length > 15){
        errors.lastName="you must enter less than 15 letters"
    }

     if (!values.email) {
        errors.email = 'you must enter an Email'
     }else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
      errors.email = 'Invalid email address'
    }

    if(!values.UserName) {
        errors.UserName='you must enter an UserName';
    }else if(values.UserName.length > 20){
        errors.UserName="you must enter less than 20 letters"
    }

    if(!values.PassWord) {
       errors.PassWord='you must enter a strong PassWord';
    }else if(values.PassWord.length < 6){
        errors.PassWord="you must enter password of atleast 6 letters"
    }

    if(!values.ConfirmPassWord) {
        errors.ConfirmPassWord='you must re-enter PassWord';
    }
   return errors;                            

}



export default connect(mapStateToProps ,{createUser})(reduxForm({
    form :'SignUpForm',
    validate
 }) (SignUpForm))
