import React from 'react';
import {Field,reduxForm} from 'redux-form';
import {createUser} from '../actions/index';
import { connect } from "react-redux";
import {Link} from 'react-router-dom';

class SignUpForm extends React.Component{

    onSubmit=(formValue)=>{

        console.log(formValue);
        this.props.createUser(formValue,(res)=>{
            if(res.status === 200){
              localStorage.setItem("token", res.data.token);
            //   toastr.success('The title', 'The message')
              this.props.history.push('/Login')  
            } else{
                alert("invalid user");
         }
           console.log(res)
        });

    }

    renderField = (formValue) => {
        return (
        <div>
            <input type={formValue.type}
            {...formValue.input} 
            className='form-control is-danger' 
            placeholder={formValue.placeholder} />
           <font color='red'>{formValue.meta.touched ? formValue.meta.error : ''}</font>
        </div>
        )
        }

    render(){

        return(

        // <form className="ui form error" onSubmit={this.props.handleSubmit(this.onSubmit)}>
        // <Field name="first_name" type="text" component={this.renderInput} label="First Name"/>
        // <Field name="last_name" type="text" component={this.renderInput} label="Last Name" />
        // <Field name="email" type="text" component={this.renderInput} label="E-mail"/>
        // <Field name="username" type="text" component={this.renderInput} label="UserName"  />
        // <Field name="password" type="password" component={this.renderInput} label="Password"/>
        // <Field name="ConfirmPassword" type="password" component={this.renderInput} label="ConfirmPassword"/>
        // <button className="ui button primary">Submit</button>

        //      </form>




        //---------------------------------------------------------------------------------------------------    
          <form onSubmit = {this.props.handleSubmit(this.onSubmit)}>
                <h1>SignUp</h1>
                <div>
                    <label>First Name</label>
                    <div>
                    <Field
                        name="first_name"
                        component={this.renderField}
                        type="text"
                        placeholder="First Name"                    
                    />
                    </div>
                </div>
                <div>
                    <label>Last Name</label>
                    <div>
                    <Field
                        name="last_name"
                        component={this.renderField}
                        type="text"
                        placeholder="Last Name"
                    />
                    </div>
                </div>  
                <div>
                    <label>Email</label>
                    <div>
                    <Field
                        name="email"
                        component={this.renderField}
                        type="email"
                        placeholder="Email"
                    />
                    </div>
                </div>
                <div>
                    <label>UserName</label>
                    <div>
                    <Field
                        name="username"
                        component={this.renderField}
                        type="UserName"
                        placeholder="UserName"
                    />
                    </div>
                </div>
                <div>
                    <label>PassWord</label>
                    <div>
                    <Field
                        name="password"
                        component={this.renderField}
                        type="PassWord"
                        placeholder="PassWord"
                    />
                    </div>
                </div>
                <div>
                    <label>ConfirmPassWord</label>
                    <div>
                    <Field
                        name="confirmpassword"
                        component={this.renderField}
                        type="PassWord"
                        placeholder="re-enter PassWord"
                    />
                    </div>
                </div>
                <div>
                    <button onClick={this.onSubmit}>
                    Submit 
                    </button>
                    <Link to="/Login" >
                        back to Login   
                    </Link>
                </div>
            </form>

        );
    }
}

const  validate = (values) => {
    const errors = {}
     if(!values.firstName) {
        errors.firstName='you must enter a firstName';
    } else if(values.firstName.length > 15){
        errors.firstName="you must enter less than 15 letters"
    }

     if(!values.lastName){
        errors.lastName='you must enter a lastName';
     }else if(values.lastName.length > 15){
        errors.lastName="you must enter less than 15 letters"
    }

     if (!values.email) {
        errors.email = 'you must enter an Email'
     }else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
      errors.email = 'Invalid email address'
    }

    if(!values.UserName) {
        errors.UserName='you must enter an UserName';
    }else if(values.UserName.length > 20){
        errors.UserName="you must enter less than 20 letters"
    }

    if(!values.PassWord) {
       errors.PassWord='you must enter a strong PassWord';
    }else if(values.PassWord.length < 6){
        errors.PassWord="you must enter password of atleast 6 letters"
    }

    if(!values.ConfirmPassWord) {
        errors.ConfirmPassWord='you must re-enter PassWord';
    }
   return errors;                            

}



export default connect(null ,{createUser})(reduxForm({
    form :'SignUpForm',
    validate
 }) (SignUpForm))
