    }

    renderField = (formValue) => {
        return (
        <div>
            <input type={formValue.type}
            {...formValue.input} 
            className='form-control is-danger' 
            placeholder={formValue.placeholder} />
           <font color='red'>{formValue.meta.touched ? formValue.meta.error : ''}</font>
        </div>
        )
        }

    render(){
        //const {handleSubmit} = this.props;
        return (

          <form onSubmit = {this.onSubmit}>
                <h3>Create Stream</h3>
                <div>
                    <label>Title</label>
                    <div>
                    <Field
                        name="Title"
                        component={renderField}
                        type="text"
                        placeholder="Title"                    
                    />
                    </div>
                </div>
                <div>
                    <label>Description</label>
                    <div>
                    <Field
                        name="content"
                        component={renderField}
                        type="textarea"
                        placeholder="description"                    
                    />
                    </div>
                </div>
                <div>
                    <label>Published by</label>
                    <div>
                    <Field
                        name="status"
                        component={renderField}
                        type="textarea"
                        placeholder="Published by"                    
                    />
                    </div>
                </div>
          </form>      
      );
   }
  }

  const mapStateToProps = (state) => {
    return state
}

export default connect(mapStateToProps , {createUser})((reduxForm({
    form : 'CreatePost'

}))(CreatePost));
