import React from 'react';
import {
    Field,
    reduxForm
} from 'redux-form';
import {
    loginUser
} from '../actions/index';
import {
    connect
} from "react-redux";
import {
    Link
} from 'react-router-dom';

class LoginForm extends React.Component {


    // componentDidMount = () => {
    //     this.props.postList((res) => {
    //       console.log(res,'callback');
    //   })
    // }

    onSubmit = (formValue) => {
        console.log(formValue, 'ashfd');
        this.props.loginUser(formValue, (res) => {
            console.log(res)
        });

    }



    renderField = (formValue) => {
        return ( <
            div >
            <
            input type = {
                formValue.type
            } {
                ...formValue.input
            }
            className = 'form-control is-danger'
            placeholder = {
                formValue.placeholder
            }
            /> <
            font color = 'red' > {
                formValue.meta.touched ? formValue.meta.error : ''
            } < /font> <
            /div>
        )
    }


    render() {

        return ( <
            form onSubmit = {
                this.props.handleSubmit(this.onSubmit)
            } >
            <
            h2 > LoginForm < /h2> <
            div >
            <
            label > UserName < /label> <
            div >
            <
            Field name = "username"
            component = {
                this.renderField
            }
            type = "UserName"
            placeholder = "UserName" /
            >
            <
            /div> <
            /div> <
            div >
            <
            label > password < /label> <
            div >
            <
            Field name = "password"
            component = {
                this.renderField
            }
            type = "PassWord"
            placeholder = "PassWord" /
            >
            <
            /div> <
            /div> <
            div >
            <
            button onClick = {
                this.onSubmit
            } >
            Login

            <
            /button> <
            Link to = "/SignUp"
            className = "ui button primary" >
            Don 't have an account?    <
            /Link> <
            /div> <
            /form>
        );
    }
}

const validate = (values) => {
    const errors = {}
    if (!values.UserName) {
        errors.UserName = 'you must enter an UserName';
    } else if (values.UserName.length > 20) {
        errors.UserName = "you must enter less than 20 letters"
    }


    if (!values.PassWord) {
        errors.PassWord = 'you must enter PassWord';
    }
    return errors;
}






export default connect(null, {
    loginUser
})(reduxForm({
    form: 'LoginForm',
    validate
})(LoginForm));
