import React from 'react' ;
import { connect } from 'react-redux' ;
import { postList } from '../actions' ;

class PostList extends React.Component {

    componentDidMount = () => {
      this.props.postList((res) => {
        console.log(res,'callback');
    })
  }



  renderAdmin(post) {
    if(post.userId === this.props.currentUserId){
        return(
             <div className="right floated content">
                <Link to={`/EditPost/${post.id}`}  className="ui button primary">Edit</Link>
                <Link to={`/DeletePost/${post.id}`} className="ui button negative">Delete</Link>
           </div>
        );    
    }
}

  renderList() {
    return this.props.map(post => {
        return (
            <div className='item' key={post.id}>
                {this.renderAdmin(post)}
                <i className="large middle aligned icon camera" />
                    <div className = 'content'>
                    <Link to={`/streams/${post.id}`} className="header">
                        {post.title}
                    </Link>
                    <div className="description">{post.description}</div>
                 </div>              
             </div>
        );
    });
}
renderCreate() {
  if(this.props.isSignedIn) {
      return (
          <div style={{ textAlign: 'right' }}>
              <Link to="/StreamCreate" className="ui button primary">
                  Create Stream    
              </Link>
          </div>
      );
  }
}

render() {
  return (
      <div> 
          <h2>Posts</h2>
      <div className='ui celled list'>{this.renderList()}</div>
      {this.renderCreate()}
      </div>
  );
}
}
const mapStateToProps = state => {
  return { 
      streams : Object.values(state.streams),
      currentUserId : state.auth.userId,
      isSignedIn:state.auth.isSignedIn
  };
};
export default connect (mapStateToProps ,{postList})(PostList);
