import React from 'react';
import { connect } from 'react-redux';
import { createPost } from '../actions';
import {Field,reduxForm} from 'redux-form';


class CreatePost extends React.Component  {

      onSubmit=(formValue)=>{

        this.props.createPost(formValue); 
        console.log(formValue); 

    }

    renderField = (formValue) => {
        return (
        <div>
            <input type={formValue.type}
            {...formValue.input} 
            className='form-control is-danger' 
            placeholder={formValue.placeholder} />
           <font color='red'>{formValue.meta.touched ? formValue.meta.error : ''}</font>
        </div>
        )
        }

    render(){

        return (

          <form onSubmit= {this.props.handleSubmit(this.onSubmit)}>
                <h3>Create Post</h3>
                <div>
                    <label>title</label>
                    <div>
                    <Field
                        name="title"
                        component={this.renderField}
                        type="text"
                        placeholder="title"                    
                    />
                    </div>
                </div>
                <div>
                    <label>description</label>
                    <div>
                    <Field
                        name="content"
                        component={this.renderField}
                        type="text"
                        placeholder="description"                    
                    />
                    </div>
                </div>
                <div>
                    <label>status</label>
                    <div>
                    <Field
                        name="status"
                        component={this.renderField}
                        type="text"
                        placeholder="Publish"                    
                    />
                    </div>
                </div>
                <div>
                    <button onClick={this.onSubmit}>
                    Submit 
                    </button>
                </div>
          </form>      
      );
   }
  }
  const validate = (formValue) => {
    const errors={};
    if(!formValue.title) {
        errors.title='you must enter a title';
    }

    if(!formValue.content){
        errors.cont='you must enter a description';
    }
    return errors;
  }


export default connect(null , {createPost})((reduxForm({
    form : 'CreatePost',
    validate

}))(CreatePost));
