import React from 'react';
import {Field,reduxForm} from 'redux-form';
import testAction from '../actions/index';
import {  connect } from "react-redux";

class SignUp extends React.Component{
    componentWillMount(){
        this.props.testAction()
    }
    onSubmit(values){
        console.log(values); 
    }

    renderField(field) {
        return (
        <div>
            <input type={field.type}
            {...field.input} 
            className='form-control is-danger' 
            placeholder={field.placeholder} />
           <font color='red'>{field.meta.touched ? field.meta.error : ''}</font>
        </div>
        )
        }


    render(){
        const {handleSubmit} = this.props;
        console.log(this.props.testReducer);

        return(
          <form onSubmit = {handleSubmit(this.onSubmit)}>
                <h1>SignUp</h1>
                <div>
                    <label>First Name</label>
                    <div>
                    <Field
                        name="firstName"
                        component={this.renderField}
                        type="text"
                        placeholder="First Name"

                    />
                    </div>
                </div>
                <div>
                    <label>Last Name</label>
                    <div>
                    <Field
                        name="lastName"
                        component={this.renderField}
                        type="text"
                        placeholder="Last Name"
                    />
                    </div>
                </div>state
                <div>
                    <label>Email</label>
                    <div>
                    <Field
                        name="email"
                        component={this.renderField}
                        type="email"
                        placeholder="Email"
                    />
                    </div>
                </div>
                <div>
                    <label>UserName</label>
                    <div>
                    <Field
                        name="UserName"
                        component={this.renderField}
                        type="UserName"
                        placeholder="UserName"
                    />
                    </div>
                </div>
                <div>
                    <label>PassWord</label>
                    <div>
                    <Field
                        name="PassWord"
                        component={this.renderField}
                        type="PassWord"
                        placeholder="PassWord"
                    />
                    </div>
                </div>
                <div>
                    <label>Confirm PassWord</label>
                    <div>
                    <Field
                        name="Confirm PassWord"
                        component={this.renderField}
                        type="PassWord"
                        placeholder="Confirm PassWord"
                    />
                    </div>
                </div>
                <div>
                    <button type="submit">
                    Submit
                    </button>
                    <button type="login">
                     Login
                    </button>
                </div>
            </form>

        );
    }
}

const  validate = (values) => {
    const errors = {}
    if (!values.email) {
      errors.email = 'Required'
    } 
     if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
      errors.email = 'Invalid email address'
    }
    return errors;                                
}



const mapStateToProps = (state) => {
    return state
}




const SignUpTest = connect(mapStateToProps , {testAction})(SignUp)

 export default reduxForm({
    form :'SignUp',
    validate
 }) (SignUpTest);
