import React from 'react';
import history from './history';
import {connect} from 'react-redux';
import {deletePost} from '../actions';
import { Link } from 'react-router-dom';

class DeletePost extends React.Component {
    componentDidMount() {
       this.props.DeletePos(this.props.match.params.id);
    }

    renderActions() {
      const {id} = this.props.match.params;
        return (
        <React.Fragment>        
            <button 
                onClick={() => this.props.deletePost(id) } 
                className="ui button negative">
                Delete
            </button>
            <Link to="/" className="ui button">
                Cancel
            </Link>
        </React.Fragment>
   );
}

    renderContent() {
        if(!this.props){
            return 'Are  you sure you want to delete this stream?'
        }

        return `Are  you sure you want to delete this stream with title: ${this.props.title}`

    }


    render() {
        return (
            <div>
                    title="Delete Post"
                    content={this.renderContent()}
                    actions={this.renderActions()}
                    onDismiss={() => history.push('/')}

            </div>
         );

    };

}

const mapStateToProps = (state,ownprops) => {
    return {stream : state.streams[ownprops.match.params.id] }
};

export default connect(mapStateToProps , {deletePost})(DeletePost);
