import React from 'react';
import {Field,reduxForm} from 'redux-form';
import testAction from '../actions/index';
import { connect } from "react-redux";
import renderField from './utils/RenderField'

class SignUp extends React.Component{
    componentWillMount(){
        this.props.testAction()
    }
    onSubmit(values){
        console.log(values); 
        localStorage.setItem("user",  JSON.stringify(values));
        var a= localStorage.getItem("user")
        console.log(a,"hdjhgd")
    }

    handleLogin = () => {
        this.props.history.push('/Login')
    }


    render(){
        const {handleSubmit} = this.props;

        return(
          <form onSubmit = {handleSubmit(this.onSubmit)}>
                <h1>SignUp</h1>
                <div>
                    <label>First Name</label>
                    <div>
                    <Field
                        name="firstName"
                        component={renderField}
                        type="text"
                        placeholder="First Name"                    
                    />
                    </div>
                </div>
                <div>
                    <label>Last Name</label>
                    <div>
                    <Field
                        name="lastName"
                        component={renderField}
                        type="text"
                        placeholder="Last Name"
                    />
                    </div>
                </div>  
                <div>
                    <label>Email</label>
                    <div>
                    <Field
                        name="email"
                        component={renderField}
                        type="email"
                        placeholder="Email"
                    />
                    </div>
                </div>
                <div>
                    <label>UserName</label>
                    <div>
                    <Field
                        name="UserName"
                        component={renderField}
                        type="UserName"
                        placeholder="UserName"
                    />
                    </div>
                </div>
                <div>
                    <label>PassWord</label>
                    <div>
                    <Field
                        name="PassWord"
                        component={renderField}
                        type="PassWord"
                        placeholder="PassWord"
                    />
                    </div>
                </div>
                <div>
                    <label>ConfirmPassWord</label>
                    <div>
                    <Field
                        name="ConfirmPassWord"
                        component={renderField}
                        type="PassWord"
                        placeholder="re-enter PassWord"
                    />
                    </div>
                </div>
                <div>
                    <button type="submit">
                    Submit
                    </button>
                    <button type="login" onClick = {this.handleLogin} >
                     Back to Login
                    </button>
                </div>
            </form>

        );
    }
}

const  validate = (values) => {
    const errors = {}

     if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
      errors.email = 'Invalid email address'
    }
console.log(values.firstName)
     if(!values.firstName || values.length<20) {
        errors.firstName='you must enter a firstName';
    }

     if(!values.lastName){
        errors.lastName='you must enter a lastName';
     }

     if (!values.email) {
        errors.email = 'you must enter an Email'
     }

    if(!values.UserName) {
        errors.UserName='you must enter an UserName';
    }

    if(!values.PassWord) {
       errors.PassWord='you must enter a strong PassWord';
    }

    if(!values.ConfirmPassWord) {
        errors.ConfirmPassWord='you must  re-enter PassWord';
     }

    return errors;                                
}



const mapStateToProps = (state) => {
    return state
}

export default connect(mapStateToProps ,{testAction})(reduxForm({
    form :'SignUpForm',
    validate
 }) (SignUp))
